-- Color tables
ct_identity,ct_transparency,ct_shadow,ct_spotlight=0x0,0x40,0x80,0xc0
function choose_color_table(mask)
	poke(0x5508,peek(0x5508)&0x3f|mask)  --draw mask
	poke(0x550b,0x3f)  --target mask for shapes 
end
