----------------------------
function _update()
 ballx+=dx
 bally+=dy
 if (ballx>=screenwidth-ballwidth or ballx==0) then 
  dx=-1*dx
  note(50,0,32,0,0,0)
  end
 if (bally>=screenheight-ballheight or bally==0) then
  dy=-1*dy
  note(50,0,32,0,0,0)
  end
end
----------------------------
function _draw()
 cls()
 -- palette blocks
 for i=0,63 do
	rectfill( i%16*30,i\16*64,i%16*30+30,i\16*64+63,i)
 end --for

 --bouncing ball
 -- Soft shadow
 draw_ball[2]()
 --spr(f_thick16,176,71)
	
 printsc("JEHKOBA 64",40,20,2,colors.white) -- string, x, y, [scale],[maincolor,lightshadow, darkshadow, outlinecolor]
 printsc("WHITE",40,100,1,colors.white) -- string, x, y, [scale],[maincolor,lightshadow, darkshadow, outlinecolor]
