-- printsc - scale and colorize
-- adapted from Professor Action's One Font Print routine
function printsc(s, x, y, sc, c4 )
   local cx,ch,p=x
   cx=x
   if (not sc or sc==nil) sc=2
   --if (c) p=pal(11, c) printh(p)
   for i=1,#s do
      ch=ord(s,i)-32
      if ch==-22 then -- line feed?
         y+=16*sc
         cx=x
      else
         -- sspr( sx, sy, sw, sh, dx, dy, [dw,] [dh,] [flip_x,] [flip_y] )
         if (c4!=nil and c4[1]!=0) then -- colorize if colors specified, otherwise white
	         pal(0x3f,c4[1]) -- white
	         pal(0x3e,c4[2]) -- lt gray
	         pal(0x35,c4[3]) -- dark gray
	         pal(0x2c,c4[4]) -- dark blue
	      end
	      sspr(f_thick16,ch%8*16,ch\8*16,16,16,cx,y,16*sc,16*sc)
	      
         cx+=16*sc
      end
   end
   if (c0) 	pal(v64,2)
end

draw_ball=
{
	function()		--Ball with Light Shadow
		choose_color_table(ct_shadow)
 		circfill( ballx+30, bally+30, 19,63+ct_shadow )
		choose_color_table(ct_identity)
		spr(ball_sprite,ballx,bally)
	end,
	function() --Ball with Medium Shadow
		choose_color_table(ct_shadow)
 		circfill( ballx+30, bally+30, 19,55+ct_shadow )
		choose_color_table(ct_identity)
		spr(ball_sprite,ballx,bally)
	end,
	function() --Ball with Dark Shadow
		choose_color_table(ct_shadow)
 		circfill( ballx+30, bally+30, 19,57+ct_shadow )
		choose_color_table(ct_identity)
		spr(ball_sprite,ballx,bally)
	end,
	function() --Dim Spotlight
		choose_color_table(ct_spotlight)
 		circfill( ballx+30, bally+30, 19,59+ct_spotlight )
		choose_color_table(ct_identity)
	end,
	function() --Medium Spotlight
		choose_color_table(ct_spotlight)
 		circfill( ballx+30, bally+30, 19,57+ct_spotlight )
		choose_color_table(ct_identity)
	end,
	function() --Bright Spotlight
		choose_color_table(ct_spotlight)
 		circfill( ballx+30, bally+30, 19,55+ct_spotlight )
		choose_color_table(ct_identity)
	end,
	function() --Transparent Ball
		choose_color_table(ct_transparency)
 		spr(ball_sprite,ballx,bally)
		choose_color_table(ct_identity)
	end,
	function() --Transparent Ball with Transparent Shadow
		choose_color_table(ct_transparency)
		circfill( ballx+30, bally+30, 19,38+ct_transparency)
		spr(ball_sprite,ballx,bally) 		
		choose_color_table(ct_identity)
	end,
}
